<?php// DADOS DE CONEXÃO ####################define('HOST','localhost');define('USER','root');define('DBSA','titan');define('PASS','mxda01');//AUTO LOAD DE CLASSES ####################function ini_class($Class) {    $cDir = ['Conn', 'Helpers'];      $iDir = NULL;    foreach($cDir as $dirName):	if (!$iDir && file_exists(__DIR__ . "/{$dirName}/{$Class}.class.php" ) && !is_dir(__DIR__ . "/{$dirName}/{$Class}.class.php")):            include_once (__DIR__ . "/{$dirName}/{$Class}.class.php");            $iDir = TRUE;             endif;            endforeach;    if(!$iDir):        trigger_error("Não foi possível carregar {$Class}.class.php ", E_USER_ERROR);               die;    endif;}spl_autoload_register("ini_class");// TRATAMENTO DE ERROS #####################define('WS_ACCEPT', 'accept');define('WS_INFOR', 'infor');define('WS_ALERT', 'alert');define('WS_ERROR', 'error');//WSErro :: Exibe erros lançados :: Frontfunction WSErro($ErrMsg, $ErrNo, $ErrDie = null) {    $CssClass = ($ErrNo == E_USER_NOTICE ? WS_INFOR : ($ErrNo == E_USER_WARNING ? WS_ALERT : ($ErrNo == E_USER_ERROR ? WS_ERROR : $ErrNo)));    echo "<p class=\"{$CssClass}\">{$ErrMsg}<span class=\"ajax_close\"></span></p>";    if ($ErrDie):        die;    endif;}//PHPErro :: personaliza o gatilho do PHPfunction PHPErro($ErrNo, $ErrMsg, $ErrFile, $ErrLine) {    $CssClass = ($ErrNo == E_USER_NOTICE ? WS_INFOR : ($ErrNo == E_USER_WARNING ? WS_ALERT : ($ErrNo == E_USER_ERROR ? WS_ERROR : $ErrNo)));    echo "<p class=\"{$CssClass}\">";    echo "<b>Erro na Linha: #{$ErrLine} ::</b> {$ErrMsg}<br>";    echo "<small>{$ErrFile}</small>";    echo "<span></span></p>";    if ($ErrNo == E_USER_ERROR):        die;    endif;}set_error_handler('PHPErro');